This sample shows how to call GetAllowedConnectionsAsync with required parameters and parse the result.

var credential = new DefaultAzureCredential();
var client = new AllowedConnectionsClient("<subscriptionId>", "<ascLocation>", credential);

await foreach (var item in client.GetAllowedConnectionsAsync(new RequestContext()))
{
    JsonElement result = JsonDocument.Parse(item.ToStream()).RootElement;
    Console.WriteLine(result.GetProperty("properties").GetProperty("calculatedDateTime").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("id").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("inboundConnectedResources")[0].GetProperty("connectedResourceId").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("inboundConnectedResources")[0].GetProperty("tcpPorts").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("inboundConnectedResources")[0].GetProperty("udpPorts").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("outboundConnectedResources")[0].GetProperty("connectedResourceId").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("outboundConnectedResources")[0].GetProperty("tcpPorts").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("connectableResources")[0].GetProperty("outboundConnectedResources")[0].GetProperty("udpPorts").ToString());
    Console.WriteLine(result.GetProperty("id").ToString());
    Console.WriteLine(result.GetProperty("name").ToString());
    Console.WriteLine(result.GetProperty("type").ToString());
}

