This sample shows how to call GetSubAssessmentsAsync with required parameters and parse the result.

var credential = new DefaultAzureCredential();
var client = new SubAssessmentsClient(credential);

await foreach (var item in client.GetSubAssessmentsAsync("<scope>", new RequestContext()))
{
    JsonElement result = JsonDocument.Parse(item.ToStream()).RootElement;
    Console.WriteLine(result.GetProperty("properties").GetProperty("id").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("displayName").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("status").GetProperty("code").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("status").GetProperty("cause").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("status").GetProperty("description").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("status").GetProperty("severity").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("remediation").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("impact").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("category").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("description").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("timeGenerated").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("resourceDetails").GetProperty("source").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("additionalData").GetProperty("assessedResourceType").ToString());
    Console.WriteLine(result.GetProperty("id").ToString());
    Console.WriteLine(result.GetProperty("name").ToString());
    Console.WriteLine(result.GetProperty("type").ToString());
}

